sysevent_in_email_action.script:
var skipmsg = false;

if (email.subject != undefined) {
	var subject = email.subject;

	if (email.subject == "CISCO GUEST ACCESS") {
		skipmsg = true;
	}

	if ((subject.toLowerCase().indexOf("out of the office") > -1) || (subject.toLowerCase().indexOf("in my absence") > -1))
		skipmsg = true;
	if ((subject.toLowerCase().indexOf("je suis absent(e)") > -1))
		skipmsg = true;
	if (subject.toLowerCase().indexOf("your message has been received by loews hotels reservations center") > -1)
		skipmsg = true;
}

if (!skipmsg) {
	current.description = "received from: " + email.origemail + "\n\n" + email.body_text;
	current.short_description = email.subject;

	var sid = gs.getUserID();

	current.caller_id = sid;
	current.opened_by = sid;
	current.u_department = current.caller_id.department;
	current.location = current.caller_id.location;
	current.u_vip = current.caller_id.vip;
	current.state = 1;
	current.notify = 2;
	current.contact_type = "email";

	if (subject.toLowerCase().indexOf("opera daily interface status") > -1) {
		current.assignment_group.setDisplayValue('Hotel IT Applications');
		current.assigned_to.setDisplayValue('Jim Tadlock (jim.tadlock)');
		gs.eventQueue("incident.assigned", current); //trigger email to Tadlock, this is not automatic on incident creation (only when assigned in an existing incident) hence the eventQueue here.
	}
	else if ((subject.toLowerCase().indexOf("relay call for") > -1)) {
		current.assignment_group.setDisplayValue('Hotel Service Desk');
		current.u_business_service.setDisplayValue('Relay Call');

	}
		else if ((subject.toLowerCase().indexOf("loews micros support") > -1)) {
		current.assignment_group.setDisplayValue('Hotel IT Micros Support');
		current.u_business_service.setDisplayValue('POS - Simphony 2');
	    current.assigned_to.setDisplayValue('Kyle Mizell (kyle.mizell)');
		current.category.setDisplayValue('Request/Service');
		//gs.eventQueue("incident.assigned", current);
		}
	else if ((subject.toLowerCase().indexOf("lhtn backup failure report") > 27) ||    (subject.toLowerCase().indexOf("bsc backup failure report") > 27) || (subject.toLowerCase().indexOf("lhwp backup failure report") > 27)|| (subject.toLowerCase().indexOf("lbb backup failure report") > 27)|| (subject.toLowerCase().indexOf("lno backup failure report") > 27)||  (subject.toLowerCase().indexOf("min backup failure report") > 27)) {
		current.assignment_group.setDisplayValue('Hotel IT Technical');
		current.u_business_service.setDisplayValue('Backup Administration');
		current.category.setDisplayValue('Request/Inquiry');
		gs.eventQueue("incident.assigned", current);
	}
	else {
		current.assignment_group.setDisplayValue('Hotel Service Desk');
	}
	current.company.setDisplayValue('Loews Hotels');

	var localITlookup = new GlideRecord('u_hotel_interface_mapping');
	localITlookup.get('u_location',current.location.sys_id);

	var localITwatchlist = new GlideRecord('sys_user_group');
	localITwatchlist.get('name', localITlookup.u_local_it_group.name);

	gs.getSession().setStrictQuery(true);
	var watchlistmembers = new GlideRecord('sys_user_grmember');
	watchlistmembers.addQuery('group.sys_id', localITwatchlist.sys_id);
	watchlistmembers.query();
	current.u_itil_watch_list = '';
	var i = 0;
	//gs.log('group id - ' + localITwatchlist.sys_id);
	//gs.log('record count - ' + watchlistmembers.getRowCount());
	while (watchlistmembers.next()) {
		//i++;
		current.u_itil_watch_list += ', ' + watchlistmembers.user;
	}

	if (subject.toLowerCase().indexOf("microspos import failed for") > -1) {
		var propertycode = subject.substring(29);
		var bs = new GlideRecord('cmdb_ci_service');
		var locale = new GlideRecord('cmn_location');
		locale.get('u_property_code',propertycode);

		var pos = new GlideRecord('u_hotel_interface_mapping');
		pos.get('u_location',locale.sys_id);

		current.u_business_service= pos.u_pos_system;
	}

	if(email.importance != undefined) {
		if(email.importance == "High") {
			current.priority = 1;
		}
	}

	if (current.caller_id.vip == true) {
		current.urgency = 1;
		current.priority = 1;
	}

	current.insert();

}
